 $(function(){
     var testObj = $('.test-foot');
     testObj.text('123456');
     console.log($('.test-foot').offset());
     testObj.css({
         position: 'absolute',
         top: testObj.offset().top,
         left: testObj.offset().left,
     });
     testObj.animate({
         top: 0,
         left:0,
         'border-radius': 0,
         'border-width': 1+ 'px',
         height: $(document).height()+'px',
         width: $(document).width()+'px',
     }, 500);
 })
